<script>
import AuthService from "../js/services/auth.js";
import store from "@/js/store.js";
import { computed } from "vue";
import { useRouter } from "vue-router";

export default {
  setup() {
    const isAuthenticated = computed(() => store.getters.isAuthenticated);
    const userDetails = computed(() => store.getters.getCurrentUserDetails);
    const router = useRouter();

    const toggleSidebar = () => {
      console.log("Toggle sidebar")
      const sidebar = document.getElementById("default-sidebar");
      const backdrop = document.getElementById("sidebar__backdrop");
      sidebar.classList.toggle("-translate-x-full");
      backdrop.classList.toggle("sidebar__backdrop");
    };

    const logoutUser = (e) => {
      e.preventDefault();
      const choice = confirm("Are you sure you want to logout?");
      if (!choice) return;
      AuthService.logout()
        .then((response) => {
          if (response.status === 200) {
            store.commit("setCurrentUser", null);
            console.log({ currentUser: store.getters.getCurrentUser });
            console.log("Logged out");
            router.push({ name: "SignIn" });
          }
        })
        .catch((error) => {
          console.error(error);
        });
    };
    return {
      isAuthenticated,
      logoutUser,
      toggleSidebar,
      userDetails,
    };
  },
};
</script>

